mapper.update:
update book set status = 1
where bookid = #{bookid}
and status in(0,2)

mapper.select:
select memberid_fk
from reservation
where bookid_fk = #{BOOKID}

select count(*)
from reservation
where bookid_fk = #{BOOKID}

select memberid, name, title, bookid, to_char(rentaldate, 'yyyy-mm-dd') as rentaldate, to_char(deadline, 'yyyy-mm-dd') as deadline, renew ,case when deadline < sysdate then to_date(to_char(sysdate, 'yyyy-mm-dd')) - to_date(to_char(deadline, 'yyyy-mm-dd')) else 0 end as deadlinecut from rental R join member M on R.memberid_fk = M.memberid join book B on R.bookid_fk = B.bookid where ${CATEGORY} like '%' || #{SEARCHWORD} || '%'
and libcode_fk = #{LIBCODE}
order by ${SORT} asc

select memberid, name, title, bookid, to_char(rentaldate, 'yyyy-mm-dd') as rentaldate, to_char(deadline, 'yyyy-mm-dd') as deadline, renew ,case when deadline < sysdate then to_date(to_char(sysdate, 'yyyy-mm-dd')) - to_date(to_char(deadline, 'yyyy-mm-dd')) else 0 end as deadlinecut from rental R join member M on R.memberid_fk = M.memberid join book B on R.bookid_fk = B.bookid left join reservation E on E.bookid_fk = B.bookid where ${CATEGORY} like '%' || #{SEARCHWORD} || '%' and nvl(to_char(E.reservedate, 'yyyy-mm-dd'), ' ') = ' '
and libcode_fk = #{LIBCODE}
order by ${SORT} asc
