mapper.select:
select RNO, libcode, idx, libname, tel, post, addr1, addr2, fileName, orgFilename, fileSize, regDate, y, x from ( select rownum as RNO, libcode, idx, libname, tel, post, addr1, addr2, fileName, orgFilename, fileSize, regDate, y, x from ( select libcode, idx, libname, tel, post, addr1, addr2, fileName, orgFilename, fileSize, regDate, y, x from library
where ${colname} like '%'||#{searchWord}||'%'
order by idx )v )t where RNO between #{rno1} and #{rno2}

select libcode, idx, libname, tel, post, addr1, addr2, fileName, orgFilename, fileSize, regDate, y, x
from library
where idx = #{idx}
