mapper.select:

	select count(*)
	from member
	where memberid = #{memberid}

mapper.update:
	
	update librarian set LIBCODE_FK = #{LIBCODE_FK},
	                     NAME = #{LIBRARIANNAME},
	                     TEL = #{LIBRARIANTEL},
	                     STATUS = #{STATUS},
	                     IMGFILENAME = #{IMGFILENAME}
	where idx = #{IDX}


	update member set revokedate = revokedate + #{delayDate}
					 ,latefee = latefee + #{delayfee}
					 ,status = 2
	where memberid = #{memberidList}
